import React from 'react';
import { StyleSheet, Text, View, ImageBackground, Image } from 'react-native';

import { screenWidth } from '../../utils/common';
console.log('screenWidth', screenWidth);

import GreenBackground from '../../assets/images/green-background.jpg';
// import MobileIcon from '../../assets/images/smartphone.svg';

export default function GetOTP() {
  return (
    <View style={styles.container}>
      <View style={styles.curvedContainer}>
      <ImageBackground style={styles.imageBackground} source={GreenBackground}>
      <Image source={require("../../assets/images/smartphone.svg")} style={styles.image} />
      <Text>Open up App.js to start working on your app!</Text>
      </ImageBackground>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    textAlign: 'center'
  },
  imageBackground: {
    width: screenWidth,
    height: 300,
    zIndex: -1,
    position: 'relative'
  },
  image: {
    width: 200,
    height: 200,
    textAlign: 'center',
  },
  curvedContainer: {
    overflow: 'hidden',
    borderBottomStartRadius: screenWidth/2,
    borderBottomRightRadius: screenWidth/2,
  }
});
