import React, { useState, useRef } from "react";
import { View, TextInput, StyleSheet } from "react-native";

import { screenWidth } from "../../utils/common";

export default function AutoFocusInput() {
  const [inputValues, setInputValue] = useState({});
  const firstInput = useRef();
  const secondInput = useRef();

  const onChangeText = (e) => {
    const { value, name } = e.target;
    console.log("input value change", value, name);
    inputValues[name] = value;
    setInputValue(inputValues);
  };

  const focusInput = (name) => {
    switch (name) {
      case 1:
        secondInput.current.focus();
        break;
      default:
        firstInput.current.focus();
        break;
    }
  };

  const inputKeyPressed = (e) => {
    setTimeout(() => {
      const { name, value } = e.target;
      console.log("input key has pressed", name, e.key);
      if ((e.key, toLowerCase() === "backspace" && !value.length))
        return focusInput(Number(name) - 2);

      focusInput(Number(name));
    }, 200);
  };

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["1"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={firstInput}
        name="1"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["2"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={secondInput}
        name="2"
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
    alignItems: "flex-start",
    justifyContent: "flex-start",
    flexDirection: "row",
  },
  textInput: {
    height: 40,
    borderBottomColor: "gray",
    borderBottomWidth: 1,
    width: screenWidth / 14,
    margin: screenWidth / 98,
    fontSize: 22,
    fontWeight: "600",
    textAlign: "center",
  },
});
