import React, { useState, useRef } from "react";
import { View, TextInput, StyleSheet, Text } from "react-native";

import { screenWidth } from "../../utils/common";

export default function AutoFocusInput() {
  const [inputValues, setInputValue] = useState({});
  const firstInput = useRef();
  const secondInput = useRef();
  const thirdInput = useRef();
  const fourthInput = useRef();
  const fifthInput = useRef();
  const sixthInput = useRef();
  const seventhInput = useRef();
  const eightInput = useRef();
  const nineInput = useRef();
  const tenInput = useRef();

  const onChangeText = (name, value) => {
    console.log("input value change", value, name);
    inputValues[name] = value;
    console.log("input values", inputValues);
    setInputValue(inputValues);
  };

  const focusInput = (name) => {
    switch (name) {
      case 1:
        return secondInput.current.focus();
      case 2:
        return thirdInput.current.focus();
      case 3:
        return fourthInput.current.focus();
      case 4:
        return fifthInput.current.focus();
      case 5:
        return sixthInput.current.focus();
      case 6:
        return seventhInput.current.focus();
      case 7:
        return eightInput.current.focus();
      case 8:
        return nineInput.current.focus();
      case 9:
        return tenInput.current.focus();
      default:
        return firstInput.current.focus();
    }
  };

  const inputKeyPressed = (e, name, value) => {
      console.log('input key pressed', name, value);
    const inputValue = value || "";
    const keyCode = e.nativeEvent.key.toLowerCase();
    if (keyCode === "backspace") {
      if (!inputValue.length) {
        setTimeout(() => {
          return focusInput(Number(name) - 2);
        }, 200);
      }
    }
    setTimeout(() => {
      if (keyCode !== "backspace" && keyCode !== "enter")
        focusInput(Number(name));
    }, 200);
  };

  return (
    <View style={styles.inputContainer}>
      <Text
        style={{
          fontSize: 22,
          paddingLeft: screenWidth / 98,
          fontWeight: "600",
        }}
      >
        +91
      </Text>
      <TextInput
        style={styles.textInput}
        onKeyPress={(e) => inputKeyPressed(e, "1", inputValues["1"])}
        onChangeText={(value) => onChangeText("1", value)}
        value={inputValues["1"]}
        maxLength={1}
        ref={firstInput}
        name="1"
      />
      <TextInput
        style={styles.textInput}
        onKeyPress={(e) => inputKeyPressed(e, "2")}
        onChangeText={(value) => onChangeText("2", value, inputValues["2"])}
        value={inputValues["2"]}
        maxLength={1}
        ref={secondInput}
        name="2"
      />
      <TextInput
        style={styles.textInput}
        onKeyPress={(e) => inputKeyPressed(e, "3")}
        onChangeText={(value) => onChangeText("3", value, inputValues["3"])}
        value={inputValues["3"]}
        maxLength={1}
        ref={thirdInput}
        name="3"
      />
      <TextInput
        style={styles.textInput}
        onChangeText={(value) => onChangeText("4", value)}
        value={inputValues["4"]}
        maxLength={1}
        onKeyPress={(e) => inputKeyPressed(e, "4")}
        ref={fourthInput}
        name="4"
      />
      <TextInput
        style={styles.textInput}
        onChangeText={(value) => onChangeText("5", value)}
        value={inputValues["5"]}
        maxLength={1}
        onKeyPress={(e) => inputKeyPressed(e, "5")}
        ref={fifthInput}
        name="5"
      />
      <TextInput
        style={styles.textInput}
        onChangeText={(value) => onChangeText("6", value)}
        value={inputValues["6"]}
        maxLength={1}
        onKeyPress={(e) => inputKeyPressed(e, "6")}
        ref={sixthInput}
        name="6"
      />
      <TextInput
        style={styles.textInput}
        onChangeText={(value) => onChangeText("7", value)}
        value={inputValues["7"]}
        maxLength={1}
        onKeyPress={(e) => inputKeyPressed(e, "7")}
        ref={seventhInput}
        name="7"
      />
      <TextInput
        style={styles.textInput}
        onChangeText={(value) => onChangeText("8", value)}
        value={inputValues["8"]}
        maxLength={1}
        onKeyPress={(e) => inputKeyPressed(e, "8")}
        ref={eightInput}
        name="8"
      />
      <TextInput
        style={styles.textInput}
        onChangeText={(value) => onChangeText("9", value)}
        value={inputValues["9"]}
        maxLength={1}
        onKeyPress={(e) => inputKeyPressed(e, "9")}
        ref={nineInput}
        name="9"
      />
      <TextInput
        style={styles.textInput}
        onChangeText={(value) => onChangeText("10", value)}
        value={inputValues["10"]}
        maxLength={1}
        onKeyPress={(e) => inputKeyPressed(e, "10")}
        ref={tenInput}
        name="10"
      />
    </View>
  );
}

const styles = StyleSheet.create({
  inputContainer: {
    width: screenWidth,
    paddingTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
  },
  textInput: {
    height: 40,
    borderBottomColor: "gray",
    borderBottomWidth: 1,
    width: screenWidth / 14,
    margin: screenWidth / 98,
    fontSize: 22,
    fontWeight: "600",
    textAlign: "center",
  },
});
