import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  Image,
  TouchableOpacity,
} from "react-native";
import { LinearGradient } from "expo-linear-gradient";

import { screenWidth, screenHeight } from "../../utils/common";

import AutoFocusInput from "../AutoFocusInput";

import GreenBackground from "../../assets/images/green-background.jpg";
import MobileIcon from "../../assets/images/smartphone.png";

export default function GetOTP() {
  const [inputValues, setInputValue] = useState({});
  const [errorInputs, toggleInputError] = useState({});
  const SubmitPhone = () => {
    console.log("phoen no. submit", inputValues);
    for (let i=1,x=10; i<=x; i++) {
      const num = i.toString();
      console.log('dgiit submit', !inputValues[num], num, typeof num);
      if (!inputValues[num]) {
        console.log('error saved', !inputValues[num], num);
        errorInputs[num] = true;
        toggleInputError(errorInputs);
      }
    }
    console.log('toggle input error', errorInputs);
  };

  return (
    <View style={styles.container}>
      <View style={styles.curvedContainer}>
        <ImageBackground
          style={styles.imageBackground}
          source={GreenBackground}
        >
          <View style={styles.subContainer}>
            <Image source={MobileIcon} style={styles.image} />
          </View>
        </ImageBackground>
      </View>
      <View style={styles.contentContainer}>
        <Text style={styles.headingText}>Phone number verification</Text>
        <Text style={styles.subText}>
          Please enter your mobile number to receive One Time Password
        </Text>
        <ImageBackground
          source={GreenBackground}
          style={{
            height: 4,
            backgroundColor: "green",
            width: screenWidth / 4,
            left: screenWidth / 2.65,
            paddingTop: 10,
            alignSelf: "stretch",
          }}
        />
        <AutoFocusInput inputValues={inputValues} setInputValue={setInputValue} errorInputs={errorInputs} toggleInputError={toggleInputError} />
        <LinearGradient colors={["#56A548", "#69B844"]} style={styles.touchBtn}>
          <TouchableOpacity onPress={SubmitPhone}>
            <Text style={styles.submitBtn}>send otp</Text>
          </TouchableOpacity>
        </LinearGradient>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
    alignItems: "flex-start",
    justifyContent: "flex-start",
    width: screenWidth,
    overflow: "hidden",
  },
  imageBackground: {
    width: screenWidth * 2,
    height: screenHeight / 2,
    zIndex: -1,
    elevation: -1,
  },
  image: {
    width: 80,
    height: 80,
    top: screenHeight / 8,
    right: screenWidth / 2,
    bottom: 0,
  },
  curvedContainer: {
    overflow: "hidden",
    borderBottomStartRadius: screenWidth * 2,
    borderBottomRightRadius: screenWidth * 2,
    right: screenWidth / 2,
    top: -screenHeight / 4,
  },
  subContainer: {
    width: screenWidth * 2,
    left: screenWidth / 2,
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  contentContainer: {
    width: screenWidth,
    top: -screenHeight / 6,
    flex: 1,
    alignItems: "center",
  },
  headingText: {
    fontSize: 24,
    lineHeight: 32,
  },
  subText: {
    padding: 20,
    fontSize: 16,
    textAlign: "center",
    lineHeight: 24,
  },
  touchBtn: {
    shadowColor: "#470000",
    shadowOffset: { width: 2, height: 4 },
    shadowOpacity: 0.1,
    paddingVertical: 20,
    width: screenWidth - screenWidth / 14,
    marginTop: 20,
    borderRadius: 40,
  },
  submitBtn: {
    textTransform: "uppercase",
    fontSize: 18,
    textAlign: "center",
    color: "white"
  },
});
