import React, { useState, useRef } from "react";
import { View, TextInput, StyleSheet } from "react-native";

import { screenWidth } from "../../utils/common";

export default function AutoFocusInput() {
  const [inputValues, setInputValue] = useState({});
  const firstInput = useRef();
  const secondInput = useRef();
  const thirdInput = useRef();
  const fourthInput = useRef();
  const fifthInput = useRef();
  const sixthInput = useRef();
  const seventhInput = useRef();
  const eightInput = useRef();
  const nineInput = useRef();
  const tenInput = useRef();

  const onChangeText = (e) => {
    const { value, name } = e.target;
    console.log("input value change", value, name);
    inputValues[name] = value;
    setInputValue(inputValues);
  };

  const focusInput = (name) => {
    switch (name) {
      case 1:
        return secondInput.current.focus();
      case 2:
        return thirdInput.current.focus();
      case 3:
        return fourthInput.current.focus();
      case 4:
        return fifthInput.current.focus();
      case 5:
        return sixthInput.current.focus();
      case 6:
        return seventhInput.current.focus();
      case 7:
        return eightInput.current.focus();
      case 8:
        return nineInput.current.focus();
      case 9:
        return tenInput.current.focus();
      default:
        return firstInput.current.focus();
    }
  };

  const inputKeyPressed = (e) => {
    const { name, value } = e.target;
    const keyCode = e.key.toLowerCase();
    if (keyCode === "backspace") {
      if (!value.length) {
        setTimeout(() => {
          return focusInput(Number(name) - 2);
        }, 200);
      }
    }
    setTimeout(() => {
      if (keyCode !== "backspace" && keyCode !== "enter")
        focusInput(Number(name));
    }, 200);
  };

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["1"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={firstInput}
        name="1"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["2"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={secondInput}
        name="2"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["3"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={thirdInput}
        name="3"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["4"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={fourthInput}
        name="4"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["5"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={fifthInput}
        name="5"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["6"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={sixthInput}
        name="6"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["7"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={seventhInput}
        name="7"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["8"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={eightInput}
        name="8"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["9"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={nineInput}
        name="9"
      />
      <TextInput
        style={styles.textInput}
        onChange={onChangeText}
        value={inputValues["10"]}
        maxLength={1}
        onKeyPress={inputKeyPressed}
        ref={tenInput}
        name="10"
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
    alignItems: "flex-start",
    justifyContent: "flex-start",
    flexDirection: "row",
  },
  textInput: {
    height: 40,
    borderBottomColor: "gray",
    borderBottomWidth: 1,
    width: screenWidth / 14,
    margin: screenWidth / 98,
    fontSize: 22,
    fontWeight: "600",
    textAlign: "center",
  },
});
